sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    const username = api.state.username;
    const password = api.state.password;

    console.log(username, password);

    // Gá»i API login (REST API)
    console.log("Starting");

    try {
        const result = await api.data.loginapi.invoke({
            username: username,
            password: password
        });
        console.log("Result:", result);
    } catch (e) {
        console.error("Error:", e);
    }

    console.log("Ended");
}
